    function addReadMore() {
      var description = document.querySelector('.product_accordion .accordion__item:first-child .accordion__content p:nth-child(4)');
      var productInfo = document.querySelector('.productView-details .productView-info');

      if (description && productInfo) {
        var fullText = description.textContent;
        var shortText = fullText.substring(0, 155) + '...';
        var shortTextContainer = document.createElement('div');
        shortTextContainer.classList.add('eg-short-text');
        shortTextContainer.textContent = shortText;

        var readMoreSpan = document.createElement('span');
        readMoreSpan.classList.add('eg-read-more');
        readMoreSpan.textContent = 'Read More';

        productInfo.insertAdjacentElement('afterend', shortTextContainer);
        shortTextContainer.insertAdjacentElement('beforeend', readMoreSpan);

        live('.eg-read-more', 'click', function () {
          var egShortText = this.parentNode;
          egShortText.classList.toggle('eg-readmore');
          if (egShortText.classList.contains('eg-readmore')) {
            egShortText.textContent = fullText;
            var fullPara = document.querySelector('.eg-readmore');
            fullPara.insertAdjacentElement('beforeend', readMoreSpan);
            readMoreSpan.textContent = 'Read Less';
          } else {
            egShortText.textContent = shortText;
            var shortPara = document.querySelector('.eg-short-text');
            shortPara.insertAdjacentElement('beforeend', readMoreSpan);
            readMoreSpan.textContent = 'Read More';
          }
        });
      }
    }

